require ("stringr")
mmwb <- function (x) {
  if(!is.vector(x)) {
    stop("Your input is not vector.")
  } else {
    return(str_extract_all(x, "(<SOS>)|(<SPC>)|(<EOS>)|(<NUM>)|([\u1000-\u1021\u1025\u1026](\u103B|\u103C)?\u103D?\u103E?\u1031?((\u102D|\u102E)?(\u102F|\u1030)?\u1032?)?(\u102B|\u102C)?[\u1036\u1037]{0,2}\u1038?[\u1000-\u1021\u1025\u1026]\u1037?(\u103A|\u1039)(\u102F|\u1030)?(\u102B|\u102C)?[\u1036\u1037]{0,2}(?>\u1038?[\u1000-\u1021\u1025\u1026]{0,1}\u1037?\u103A(\u102F|\u1030)?(\u102B|\u102C)?[\u1036\u1037]{0,2})?\u1038?)|((?<!\u102B)[\u1000-\u1021\u1025\u1026](\u103B|\u103C)?\u103D?\u103E?\u1031?(\u102B|\u102C)?((\u103A|\u1039)|((\u102D|\u102E)?(\u102F|\u1030)?\u1032?))?(?!\u102C)(\u102B|\u102C)?[\u1036\u1037]{0,2}\u1038?)|([\u1040-\u1049]+)|([\u1023-\u1027\u1029\u102A\u103F\u104C-\u104F])|([a-zA-Z]+)|([\u0021-\u003B\u003D\u003F-\u0040\\u005B-\u0060\u007B-\u0FFF]+)"))
  }
}
